    void drawTextSquare(byte xpos, byte ypos, byte w, byte h,char title[], char text[]){
        char a[strlen(title)];  
        strcpy(a, title);  
        char b[strlen(text)];  
        strcpy(b, text);  
        display.fillRect(xpos-2, ypos-11, w+1, h+1, BLACK);
        display.setTextSize(0);
        display.setTextColor(WHITE);
        display.setCursor(xpos,ypos-10);
        display.print(a);
        display.fillRect(xpos-2, ypos-1, w, h, WHITE);
        display.setTextSize(0);
        display.setTextColor(BLACK);
        display.setCursor(xpos,ypos);
        display.print(b);
        display.display();
}
